export type TouristPlace = {
  id: string
  province: string
  name: string
  category: "nature" | "temple" | "culture" | "food" | "beach" | "viewpoint"
  description: string
  location: { lat: number; lng: number }
  imageUrl?: string
}

// ─── Helper ───────────────────────────────────────────────────────────────────
function p(
  id: string,
  province: string,
  name: string,
  category: TouristPlace["category"],
  description: string,
  lat: number,
  lng: number,
  imageUrl?: string,
): TouristPlace {
  return { id, province, name, category, description, location: { lat, lng }, imageUrl }
}

