const PROVINCE_ALIASES: Record<string, string> = {
  กรุงเทพมหานคร: "กรุงเทพมหานคร",
  กรุงเทพ: "กรุงเทพมหานคร",
  อยุธยา: "พระนครศรีอยุธยา",
}

function fallbackCoordinate(province: string) {
  const seed = province.split("").reduce((sum, char) => sum + char.charCodeAt(0), 0)
  return {
    lat: Number((13.2 + ((seed % 61) - 30) * 0.1).toFixed(6)),
    lng: Number((100.8 + ((seed % 71) - 35) * 0.1).toFixed(6)),
  }
}

function resolveProvinceCoordinate(province: string) {
  const normalizedProvince = PROVINCE_ALIASES[province] ?? province
  const matchedTrip = TRIPS.find((trip) => {
    const tripProvince = PROVINCE_ALIASES[trip.to] ?? trip.to
    return normalize(tripProvince) === normalize(normalizedProvince)
  })
  return matchedTrip ? matchedTrip.destinationLocation : fallbackCoordinate(normalizedProvince)
}

function toId(value: string) {
  return value
    .normalize("NFKD")
    .replace(/[\u0300-\u036f]/g, "")
    .replace(/\s+/g, "-")
    .replace(/[^a-zA-Z0-9ก-๙-]/g, "")
    .toLowerCase()
}

function offsetCoordinate(base: { lat: number; lng: number }, index: number) {
  return {
    lat: Number((base.lat + (index - 1.5) * 0.045).toFixed(6)),
    lng: Number((base.lng + (1.5 - index) * 0.052).toFixed(6)),
  }
}

const realProvinces = new Set(REAL_PLACES.map((p) => p.province))

function buildFallbackPlaces() {
  const places: TouristPlace[] = []
  for (const province of THAI_PROVINCES) {
    if (realProvinces.has(province)) continue
    const location = resolveProvinceCoordinate(province)
    PLACE_TEMPLATES.forEach((template, index) => {
      places.push({
        id: `${toId(province)}-${index + 1}`,
        province,
        name: `${template.namePrefix}${province}`,
        category: template.category,
        description: template.description,
        location: offsetCoordinate(location, index),
      })
    })
  }
  return places
}

const ALL_PLACES: TouristPlace[] = [...REAL_PLACES, ...buildFallbackPlaces()]

export function getAllTouristPlaces() {
  return ALL_PLACES
}

export function getTouristPlacesByIds(ids: string[]): typeof ALL_PLACES {
  const idSet = new Set(ids)
  return ALL_PLACES.filter((p) => idSet.has(p.id))
}

export function searchTouristPlaces(params: {
  province?: string
  q?: string
  category?: TouristPlace["category"]
  limit?: number
}) {
  const { province, q, category, limit = 100 } = params
  const normalizedProvince = province ? normalize(province) : ""
  const normalizedQuery = q ? normalize(q) : ""
  const filtered = ALL_PLACES.filter((place) => {
    const byProvince = normalizedProvince ? normalize(place.province).includes(normalizedProvince) : true
    const byQuery = normalizedQuery ? normalize(`${place.name} ${place.description}`).includes(normalizedQuery) : true
    const byCategory = category ? place.category === category : true
    return byProvince && byQuery && byCategory
  })
  return filtered.slice(0, Math.min(Math.max(limit, 1), 1000))
}
